mapper.select:
select * from member


		member_phone = #{memberPhone} and member_name = #{memberName} and


		member_id = #{memberId}

select * from (select rownum rn,board.* from (select * from member
where ${searchKeyword} like '%'||#{searchValue}||'%'
order by member_no desc) board) where rn between #{startRow} and #{endRow}
