mapper.select:
select * from notice_service where 
notice_service_no = #{notice_service_no}
and notice_service_sortation = #{notice_service_sortation}
and member_no = #{member_no} order by notice_service_no desc

select * from (select rownum rn,board.* from 
(select * from notice_service where notice_service_sortation = #{notice_service_sortation} order by notice_service_no desc)
board) where rn between #{startRow} and #{endRow}
