mapper.insert:

	select seq_pt_pricing.nextval from dual

insert into ptpricing values(#{pt_pricing_no}, #{trainer_no}, #{pt_pricing_round}
	, #{pt_pricing_price}, #{pt_pricing_status})

mapper.update:
update ptpricing

	
		pt_pricing_round=#{pt_pricing_round},
	
	
		pt_pricing_price=#{pt_pricing_price},
	
	
		pt_pricing_status=#{pt_pricing_status}
	

where trainer_no=#{trainer_no}
