mapper.update:
update trainer

	
		trainer_profile_img=#{trainer_profile_img}
	
	
		trainer_profile_img=#{trainer_profile_img}
	
	
		trainer_addresss2=#{trainer_addresss2}
	
	
		trainer_zip=#{trainer_zip}
	

	where trainer_no = #{trainer_no}

mapper.select:
select * from (select rownum rn,board.* from (select * from member m right join trainer t on m.member_no = t.member_no
m.member_id like '%'||#{member_id}||'%'
m.member_name like '%'||#{member_name}||'%'
and m.member_status = #{member_status}
order by m.member_no desc) board) where rn between #{startRow} and #{endRow}
